X = iris.data[:,:1]
y = iris.target

# Creamos un filtro para quedarnos solo con dos clases (clasificación binaria)
filter = (y == 0) | (y == 1)
X = X[filter]
y = y[filter]

X_train, X_test, y_train, y_test = train_test_split(X, y, test_size=0.3, random_state=42)  # test_size=0.3 significa que el 30% de los datos se utilizará como conjunto de prueba

def model(X_train, Y_train, X_test, Y_test, iterations=2000, eta=0.5):
    """
    Construye el modelo de regresión logística llamando a las funciones implementadas anteriormente.

    Argumentos:
    X_train -- conjunto de entrenamiento
    Y_train -- etiquetas de entrenamiento
    X_test -- conjunto de prueba
    Y_test -- etiquetas de prueba
    num_iterations -- hiperparámetro que representa el número de iteraciones para optimizar los parámetros
    eta -- hiperparámetro que representa la tasa de aprendizaje usada en la regla de actualización de optimize()

    Devuelve
    d -- diccionario que contiene información sobre el modelo.
    """

    # Inicializar parámetros con valores normales
    p = X_train.shape[1]  # Número de características
    weights = initialize_parameters_normal(p)

    # Descenso del gradiente
    weights, grads, costs = optimize(weights, X_train, Y_train, iterations, eta)

    # Predicciones para los conjuntos de entrenamiento y prueba
    y_prediction_test = predict(weights, X_test)
    y_prediction_train = predict(weights, X_train)

    # Calcular la precisión de entrenamiento y de prueba
    train_accuracy = calculate_accuracy(Y_train, y_prediction_train)
    test_accuracy = calculate_accuracy(Y_test, y_prediction_test)

    # Imprimir la precisión de entrenamiento y de prueba
    print("Accuracy de entrenamiento: {:.2f} %".format(train_accuracy))
    print("Accuracy de prueba: {:.2f} %".format(test_accuracy))

    # Crear un diccionario con los resultados y parámetros del modelo
    d = {
        "costs": costs,
        "Y_prediction_test": y_prediction_test,
        "Y_prediction_train": y_prediction_train,
        "weights": weights,
        "learning_rate": eta,
        "num_iterations": iterations
    }

    return d

####VERIFICACIÓN DE LA FUNCIÓN#######
